import React from 'react';

const styles = {
  root: {
    width: '20%',
    margin: 'auto',
    marginTop: 16,
    padding: 16,
    textAlign: 'left',
    backgroundcolor: 'white',
    borderRadius: 16
  },
  imageContainer: {
    disaply: 'inline-block',
    width: '50',
  },
  image: {
    width: 50,
    height: 50,
    borderRadius: 25
  },
  commentContainer: {
    display: 'inline-block',
    marginLeft: 16,
    textAlign: 'left',
    verticalAlign: 'top'
  },
  nameText: {
    color: 'black',
    fontSize: '20'
  }
}



class Comment extends React.Component {
  render() {
    return (
      <div style={styles.root}>
        <div style={styles.imageContainer}></div>
      </div>
    )
  }
}

export default Comment;
